fn longest_valid_parentheses(s: String) -> i32 {
   if s == "" {
    return 0;
    }
    let mut f: bool = false;
    let mut v: Vec<char> = vec![];
    for c in s.chars(){
      //  println!("{}",c);
        match c {
            '(' => {
                v.push(c);
            }
            ')' => {
                let popped: Option<char> = v.pop();

                match popped {
                    Some(popped) => {

                        if popped != char::from_u32(c as u32 - 1).unwrap() {
                           f= true;
                           break;
                        }
                    }
                None => {
                    f=true;
                    break
                }
                }
            }
            _ => ()
        }
    }
    if !f && v.len() == 0{
        return s.len() as i32
    }
    let e1 = longest_valid_parentheses(s[1..].to_string());
    let e2 = longest_valid_parentheses(s[..s.len()-1].to_string());
    if e1 > e2 { e1 }
    else { e2 }
}

fn is_valid(s: String) -> i32 {
let mut score: Vec<i32> = Vec::new();
    if s == ""{
        return 0
    }
    for i in 0..s.len()-1 {
    for j in i..s.len() {
    let mut v: Vec<char> = vec![];
    //let mut l: i32 = 0;
    let mut f: bool = false;
        //    println!("{}, {}, {}",i,j,s.len());
    for c in s[i..j+1].chars(){
      //  println!("{}",c);
        match c {
            '(' => {
                v.push(c);
            }
            ')' => {
                let popped: Option<char> = v.pop();

                match popped {
                    Some(popped) => {

                        if popped != char::from_u32(c as u32 - 1).unwrap() {
                           f= true;
                           break;
                        }
                    }
                None => {
                    f=true;
                    break
                }
                }
            }
            _ => ()
        }
    }
    //println!("f: {}, v.len: {}", f, v.len());
    if !f && v.len()==0{
    score.push(s[i..j+1].len() as i32);
    }
    }
    }
   // println!("{:?}",score);

    let max_value = score.iter().max();
    match max_value {
    Some(max) => *max,
    None      => 0,
    }
    }
